package com.learnjava.topic29.wrapperclass;
public class Demo_WrapperClass_252_AutoBoxing {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		Integer num = new Integer(10);
		Integer i =10;//how can assign primitve value to class object .//auto boxing 
		//Integer count = new Integer(10);
		System.out.println(i);
		
		int j =new Integer(10); // auto unboxing ,converting the object to primitive
		System.out.println(j); 

	}

}
